// Check for required fields
for (const item of items) {
  if (!item.json.description) {
    item.json.description = 'Missing description - please review';
    item.json._needs_review = true;
  }
}
return items;

// Log the entry
const results = [];
for (const item of items) {
  results.push({
    timestamp: new Date().toISOString(),
    entry: item.json,
    status: item.json._needs_review ? 'needs_review' : 'processed'
  });
}
return {json: results};
